class CliqueTree(object):

    def __init__(self, cliqueList, edges):
        self.cliqueList = copy.deepcopy(cliqueList)
        self.edges = edges

    def _get_netxt_cliques(self, messages):
        """Finds ready cliques in the clique tree using the matrix of
        current message (n by n matrix of factors)
        Returns index of clique that sends message and index of clique
        that recieves message
        """
        for i in range(len(messages)):
            neighbours = np.where(self.edges[i,:] == 1)[0].tolist()
            neighbours.remove(i)
            for j in neighbours:
                if messages[i][j] is None:
                    #had Ci recived messages from all its neighbours expect Cj?
                    good = True
                    for k in neighbours:
                        if not k == j and messages[k][i] is None:
                            good = False
                            break
                    if good:
                        return [i,j]
        return [None, None]

    def calibrate(self, max_sum = False):
        """Calibrates the clique tree using message passing.
        Uses sum product message passing as default and max sum message passing
        when max_sum flag is set
        """
        # initialize
        n = len(self.cliqueList)
        messages = [[None for _ in range(n)] for _ in range(n)]
        operation_prod = add if max_sum else mul
        operation_marg = max if max_sum else add

        # go to log space if map search
        if max_sum:
            for c in self.cliqueList:
                c._val = np.log(c._val)

        while True:
            i, j = self._get_netxt_cliques(messages)
            if i is None or j is None:
                break
            # Read messages and update belief
            belief = copy.deepcopy(self.cliqueList[i])
            for message in [messages[k][i] for k in range(n) if not k == j]:
                if message is None: continue
                belief = factor.factor_product(belief, message, operation_prod)
            # Send message
            var = [v for v in belief.var if not v in self.cliqueList[j].var]
            messages[i][j] = copy.deepcopy(factor.factor_marginalization(belief, var[0] if len(var) > 0 else None, operation_marg))
            for v in var[1:]:
                messages[i][j] = copy.deepcopy(factor.factor_marginalization(messages[i][j], v, operation_marg))

        # after calibration, compute final potentials for all cliques
        for i in range(n):
            for message in [messages[j][i] for j in range(n)]:
                if message is None: continue
                self.cliqueList[i] = copy.deepcopy(factor.factor_product(self.cliqueList[i],
                                                            message, operation_prod))


    @classmethod
    def create_clique_tree(cls, factors):
        """ """
        # Create a list of al variables and the coressponding cardinalitys
        variables = list(set([var for f in factors for var in f.var]))
        card = []
        for var in variables:
            for f in factors:
                if var in f.var:
                    card.append(f.card[f.var.index(var)])
                    break

        # connect the edges
        edges = np.zeros((len(variables), len(variables)))
        for f in factors:
            for var1 in f.var:
                for var2 in f.var:
                    edges[variables.index(var1), variables.index(var2)] = 1

        # Use min-neighbors: eliminate the var that has the smalles number of
        #edges connected to it.
        for _ in range(len(variables)): # do n times
            best_clique = 0
            best_score = np.inf
            for i, edge in enumerate(edges):
                score = sum(edge)
                if score > 0 and score < best_score:
                    best_score = score
                    best_clique = i
            factors, variables, edges = CliqueTree.eliminate_var(factors, variables, edges, best_clique)

        tree = CliqueTree()
        tree.prune_tree()
        tree.Compute_initial_potentials



def map_singelton(factors):
    """Takes a list of factors reduced to only one variable and returns the most
    probable assigments (maximum a poserior) to these factors.
    """
    return [int(np.argmax(f._val)) + 1 for f in factors]


